[R26] {'review_id': 'mAZWzwN3pOzv6SdY-73t1Q', 'user_id': 'zMpvGbNYAHasx-ZxN_hFQw', 'business_id': 'Bih0eT_lbbCUwKmGYpX0hw', 'stars': 5.0, 'useful': 2, 'funny': 0, 'cool': 0, 'text': "Strait up gained 15-pounds after moving across the street!  This place was amazing to live near...not for my clothing, or body shape (unless rounder & lumpier is what you're into) but great all the same!  My two go-to's:\n- Old Fashioned Italian\n- Honey Turkey & Prosciutto w/ Swiss\n\nI have since moved out of Philadelphia and I still dream about the Old Fashioned Italian.  May be the best sandwich I've ever had.", 'date': '2019-01-05 04:44:04', 'user': {'name': 'Nathan', 'review_count': 31, 'yelping_since': '2013-03-17 17:37:22', 'elite': '', 'average_stars': 3.77, 'fans': 1}}
