[R26] {'review_id': 'DfiPMOyAVHL0S34gZGGzyw', 'user_id': 'dxjynnyJJfazwAiM1WUZdQ', 'business_id': 'gKZuvwFRHcO2Y6XfyWO7lA', 'stars': 4.0, 'useful': 0, 'funny': 0, 'cool': 0, 'text': "Went here on a recommendation and glad we went here. The food was delicious from the appetizers to the desert. A nice BYOB with a nice quaint romantic setting. Plenty of seating in the front and in the back. Portions of food was perfect and the staff is very nice. The owner even waits on the tables along with the manager. They take the time to talk to you. This place by far is one of the best BYOB's in the Queens Village area. Definitely going back.", 'date': '2012-08-12 03:14:38', 'user': {'name': 'Ken', 'review_count': 1, 'yelping_since': '2012-08-12 02:52:24', 'elite': '', 'average_stars': 4.0, 'fans': 0}}
