[R1] {'review_id': '_42vBYstF74kA3M-2FXr0g', 'user_id': 'kkoCxmKyqhEPmFxny4DHrQ', 'business_id': 'zDSZ1o-FYC2U7rrBg_kH7w', 'stars': 5.0, 'useful': 2, 'funny': 2, 'cool': 2, 'text': 'Had a lovely evening as our local cafe reopened for business. Delicious chicken, with a lovely arugula salad. The salmon skin was crisped to perfection! Unpictured also would recommend the creamy chicken soup. Will definitely be back!', 'date': '2021-12-04 03:19:46', 'user': {'name': 'Ashley', 'review_count': 9, 'yelping_since': '2019-03-28 15:39:08', 'elite': '', 'average_stars': 5.0, 'fans': 0}}
