[R24] {'review_id': 'VjzDKpwb2067NLbmgT_rWg', 'user_id': 'bnLYF7_lu1pfcD623L-BRQ', 'business_id': 'JtDZvV3HddxFnNdrwrWh7g', 'stars': 5.0, 'useful': 0, 'funny': 0, 'cool': 0, 'text': "Julio was an absolute delight and the food was delicious. The staff was extremely accommodating and pleasant, and the establishment felt safe and clean. I can't wait to come back again!", 'date': '2020-09-23 00:43:19', 'user': {'name': 'Brittany', 'review_count': 6, 'yelping_since': '2015-08-24 02:25:43', 'elite': '', 'average_stars': 3.0, 'fans': 0}}
